Create Table actions(action_id INT PRIMARY KEY NOT NULL AUTO_INCREMENT, action_type VARCHAR(20), other_stuff VARCHAR(20), start_action DATE NOT NULL, end_action DATE NOT NULL, schedule TIME NOT NULL, action_town VARCHAR(20), action_location VARCHAR(20), other_location VARCHAR(20), product VARCHAR(100), speaker BOOLEAN, speaker_suggestion VARCHAR(20) Default Null, speaker_transfer BOOLEAN NOT NULL DEFAULT 0, speaker_accommodation BOOLEAN NOT NULL DEFAULT 0, meeting_agenda VARCHAR(300) DEFAULT NULL, meeting_theme VARCHAR(50), pax_number INT NOT NULL, action_field VARCHAR(100), invited_doctors VARCHAR(300), other_doctors VARCHAR(20), comments VARCHAR(100));